 \draw[black] (\x, 0.15)--++(0, -.3) node[below, fill=white, inner sep=1pt]{\scriptsize $\xi\degree$};
\foreach \y  in {-2, -1, 1, 2}
 \draw[black] (0.08,\y)--++(-.16,0) node[left, fill=white, inner sep=1pt]{\footnotesize \y};

