\foreach \x  [evaluate =\x as \xi using ( \x /6 )] in {3,6}
 \draw[black] (\x, 0.15)--++(0, -.3) node[below, fill=white, inner sep=1pt]{\scriptsize \xi};
\foreach \y [evaluate=\y as \yi using int(4*\y)] in {1, 2, 3}
