{
\draw[black,  thick] ($ \x *(0,1) +(0.2,0) $) --++(-0.4,0); 
};
\draw[domain={0:36},smooth,variable=\x,red,very thick] plot ({\x},{ 10*sin(deg(\x *pi/18)) });
\end{tikzpicture}


