\foreach \y [evaluate=\y as \yi using (\y *.2)]  in {-10, -5, 5, 10} \draw[black,thick] (18.08, \yi) -- ++(-.16,0) node[left, xshift=-2, fill=white, inner sep=0] {$\y$};

