\coordinate (A) at (-5,8);
\coordinate (B) at (2,-3.2);
\coordinate (C) at (-7,11.2);
\coordinate (O) at (0,0);

\draw[blue, thick] (B)--(C);

\filldraw[red] (A) ellipse (6pt and 12pt) node[anchor=north east,xshift=0,yshift=0] {$(-5,8)$};
\filldraw[red] (O) ellipse (6pt and 12pt)  ;
\end{tikzpicture}


