\draw[black, thick, ->,>=stealth'] (0,-2.25)--(0,2.3);
\draw[black, thick, ->,>=stealth'] (-2.3,0)--(3,0);
\draw[red, thick, ->,>=stealth'] (0.4,0) arc (0:50:0.4) node[right, midway] {$\footnotesize \theta$};

