\draw[black,thick] (2*pi,0.1)--++(0,-.2) node[below, yshift=-4, fill=white, inner sep=1, scale=.8] {$2\pi$};
\draw[samples=65, domain=-pi:5*pi/2, variable=\x, smooth,red, very thick] plot(\x,{4*cos( deg(\x-pi/6))});
\end{tikzpicture}


