\foreach \y in {-1,1} \draw[black,thick] (.1,\y)--++(-.2,0) node[left, xshift=2, yshift=-2, scale=.9]{\y};
\draw[samples=65,domain=0:2*pi, variable=\x, smooth, red, thick] plot(\x,{cos(deg(2*\x))});
