        \draw[black,thick,->,>=stealth'] (0,-3)--(0,7.3) node[above, scale=.9] {$y$};
        \foreach \x in { -2, 2, 4,6} {
          \draw[black,thick] (\x, .08)--++(0,-.16) node[below, yshift=-2, fill=white, inner sep=1, scale=.8] {\x};
