\draw[red, very thick,->,>=stealth'] (O)--(x);
\draw[red, very thick,->,>=stealth'] (O)--(y);
\node at ($ (O) + (.6,-.7) $) {$v_x > 0$};
\node at ($ (O) + (.6,-1.2) $) {$v_y > 0$};

